mapper.update:
update rboard
   set rudate = systimestamp, 
       rcontent = #{rcontent}
 where rnum = #{rnum}

mapper.insert:
INSERT INTO rboard ( rnum, bnum, rid, rnickname, rcontent, rgroup, rstep, rindent, prnum, prid, prnickname ) VALUES ( rboard_rnum_seq.NEXTVAL, #{bnum}, #{rid}, #{rnickname}, #{rcontent}, #{rgroup}, #{rstep} + 1, #{rindent} + 1, #{prnum}, #{prid}, #{prnickname}
)
select rboard_rnum_seq.currval from dual

mapper.update:
update rboard
   set rstep  = rstep + 1
 where rgroup = #{rgroup} and rstep > #{rstep}

mapper.select:
select count(rnum) 
  from rboard_vote
 where rnum = #{rnum}
   and bnum = #{bnum}
   and rid  = #{rid}

mapper.update:
update rboard 
   set vote  = #{vote}
       udate = systimestamp
 where rnum = #{rnum}
   and bnum = #{bnum}
   and rid  = #{rid}
